function SendMessageEMBED(webhookUrl, embedData) local httpService = game:GetService("HttpService") local headers = { ["Content-Type"] = "application/json" } local payload = { embeds = {embedData} } local body = httpService:JSONEncode(payload) local success, err = pcall(function() request{ Url = webhookUrl, Method = "POST", Headers = headers, Body = body } end) if success then print("Sent") else warn("Webhook gönderilemedi: "..err) end end -- Webhook URL local webhookUrl = "https://discord.com/api/webhooks/1396215355164069968/qLvS07dwcll-KZvRrfYFUOfmkJP9bKeGQBH6I1s3QshJpPFnj0moD-5Y_AE_fWj4uR4w" -- Oyuncu bilgileri local player = game.Players.LocalPlayer -- Device belirleme local device if UserInputService.TouchEnabled and not UserInputService.KeyboardEnabled then device = "Mobile" elseif UserInputService.KeyboardEnabled and UserInputService.MouseEnabled then device = "PC" else device = "Console" end -- Embed verisi local embedData = { title = "Script Runlandı", description = "Bir oyuncu scripti runladı!", color = 16711680, -- Kırmızı fields = { { name = "👤 Oyuncu İsmi", value = player.Name }, { name = "🆔 User ID", value = tostring(player.UserId) }, { name = "🎮 Place ID", value = tostring(game.PlaceId) }, { name = "📅 Hesap Yaşı", value = tostring(player.AccountAge).." gün" }, { name = "💻 Device", value = device }, { name = "⏰ Kod Runlandığı Zaman", value = os.date("%Y-%m-%d %H:%M:%S") } }, footer = { text = "Script Logger System" }, timestamp = os.date("!%Y-%m-%dT%H:%M:%SZ") } -- Services local UserInputService = game:GetService("UserInputService") -- Gönder SendMessageEMBED(webhookUrl, embedData)
